mapper.select:
/* DBRD2100.SELECT_ABNRML_ATM_LIST : Search Abnormal ATM List (접속이상 ATM 목록 조회) */ SELECT TOP 100 PERCENT TMNL_NO /* Terminal number(터미널번호) */ , IP_ADDR /* iP address(IP주소) */ , LST_USE_DTM /* Last use datetime(최종사용일시) */ FROM T_TMNL_INFO WITH(NOLOCK)
WHERE (ST_CNFM_CYC * 3) < ABS(DATEDIFF(MI, GETUTCDATE(), CAST(SUBSTRING(LST_USE_DTM, 1, 8) + ' ' + SUBSTRING(LST_USE_DTM, 9, 2) + ':' + SUBSTRING(LST_USE_DTM, 11, 2) + ':' + SUBSTRING(LST_USE_DTM, 13, 2) AS DATETIME)))
ORDER BY LST_USE_DTM, TMNL_NO
